describe("NavigatorGeolocation",function(){var b,a;navigator.geolocation=jasmine.createSpy("geolocation");beforeEach(module("ngMap"));beforeEach(inject(function(c,d){b=c,a=d}));describe("getCurrentPosition function",function(){beforeEach(function(){var c=function(d,e){d("GOOD")};navigator.geolocation.getCurrentPosition=jasmine.createSpy().andCallFake(c)});it("Should return a promise",function(){var c=a.getCurrentPosition();expect(typeof c.then).toBe("function")});it("Should call getCurrentPosition to retrieve good results",function(){var d=jasmine.createSpy("success");var c=jasmine.createSpy("error");a.getCurrentPosition().then(d,c);b.$apply();expect(d).toHaveBeenCalled();expect(c).not.toHaveBeenCalled();expect(d).toHaveBeenCalledWith("GOOD")})});describe("getCurrentPosition function",function(){beforeEach(function(){var c=function(d,e){e("BAD")};navigator.geolocation.getCurrentPosition=jasmine.createSpy().andCallFake(c)});it("Should call getCurrentPosition to retrieve bad results",function(){var d=jasmine.createSpy("success");var c=jasmine.createSpy("error");a.getCurrentPosition().then(d,c);b.$apply();expect(d).not.toHaveBeenCalled();expect(c).toHaveBeenCalled();expect(d).not.toHaveBeenCalled()})})});